#include <bits/stdc++.h>

using namespace std;


int rowSum(vector<vector<int>>& grid, int startx, int starty, int l) {
    int oldSum = -1;
    for (int i = 0; i < l; i++) {
        int sum = 0;
        for (int j = 0; j < l; j++) {
            sum += grid[startx + i][starty + j];
        }
        if(oldSum == -1) {
            oldSum = sum;
        }
        if(oldSum != sum) {
            return -1;
        }
    }
    return oldSum;
}

int colSum(vector<vector<int>>& grid, int startx, int starty, int l) {
    int oldSum = -1;
    for (int i = 0; i < l; i++) {
        int sum = 0;
        for (int j = 0; j < l; j++) {
            sum += grid[startx + j][starty + i];
        }
        if(oldSum == -1) {
            oldSum = sum;
        }
        if(oldSum != sum) {
            return -1;
        }
    }
    return oldSum;
}

int firstDiagonal(vector<vector<int>>& grid, int startx, int starty, int l) {
    int sum = 0;
    for (int i = 0; i < l; i++) {
        sum += grid[startx + i][starty + i];
    }
    return sum;
}
int secondDiagonal(vector<vector<int>>& grid, int startx, int starty, int l) {
    int sum = 0;
    for (int i = 0; i < l; i++) {
        sum += grid[startx + i][starty + l  - i - 1];
    }
    return sum;
}



bool isMagicSquare(vector<vector<int>>& grid, int startx, int starty, int l) {
    cout << "Called isMagicSquare with " << startx << " " << starty << endl;

    int row = rowSum(grid, startx, starty, l);
    int col = colSum(grid, startx, starty, l);

    cout << row << endl;
    cout << col << endl;
    cout << firstDiagonal(grid, startx, starty, l) << endl;
    cout << secondDiagonal(grid, startx, starty, l) << endl;

    if(row != col) return false;
    if(row != firstDiagonal(grid, startx, starty, l)) return false;
    if(row != secondDiagonal(grid, startx, starty, l)) return false;
    return true;
}
int largestMagicSquare(vector<vector<int>>& grid) {
    int maxL = 1;
    int maxPossibleL = min(grid.size(),grid[0].size());
    for (int l = 16; l <= maxPossibleL; l++) {
        cout << "Trying l " << l << endl;
        for (int i = 0; i <= grid.size() - l; i++) {
            for (int j = 0; j <= grid[0].size() - l; j++) {
                if(isMagicSquare(grid, i, j, l)) {
                    maxL = max(maxL, l);
                }
            }
        }
        
    }

    return maxL;
}


